import React from "react";
import EosLogo from "../Assets/eos-logo.png";
import AbernathyLogo from "../Assets/abernathy-logo.png";

function StampPreview(props) {
  const {
    jobName,
    jobCode,
    preparedFor,
    preparedBy,
    date,
    isRevision,
    jobPhase,
    gradientStyle,
    dateFormat,
    revisionNumber,
  } = props;

  const formatDisplayDate = (date, format) => {
    if (!date || !date.year || !date.month || !date.day) {
      return "No Date Selected";
    }
    const paddedMonth = String(date.month).padStart(2, "0");
    const paddedDay = String(date.day).padStart(2, "0");
    switch (format) {
      case "YYYY/MM/DD":
        return `${date.year}/${paddedMonth}/${paddedDay}`;
      case "MM/DD/YYYY":
        return `${paddedMonth}/${paddedDay}/${date.year}`;
      case "DD/MM/YYYY":
        return `${paddedDay}/${paddedMonth}/${date.year}`;
      default:
        return `${date.year}-${paddedMonth}-${paddedDay}`; // Default format
    }
  };

  const displayDate = formatDisplayDate(date, dateFormat);

  const displayLogo =
    preparedBy === "Eos Lightmedia"
      ? EosLogo
      : preparedBy === "Abernathy Lighting Design"
      ? AbernathyLogo
      : null;

  const getGradientStyle = (gradient) => {
    switch (gradient) {
      case "Purple/Blue":
        return { background: "linear-gradient(to right, purple, blue)" };
      case "Yellow/Green":
        return { background: "linear-gradient(to right, yellow, green)" };
      default:
        return {};
    }
  };

  const previewStyle = getGradientStyle(gradientStyle);

  return (
    <div style={previewStyle}>
      <h2>Stamp Preview</h2>
      <p>Job Name: {jobName}</p>
      <p>Job Code: {jobCode}</p>
      <p>Prepared For: {preparedFor}</p>
      <p>Prepared By: {preparedBy}</p>
      <p>
        {isRevision ? "Revision Date" : "Issued Date"}: {displayDate}
      </p>
      {isRevision && <p>Revision Number: {revisionNumber}</p>}
      <p>Job Phase: {jobPhase}</p>
      {displayLogo && <img src={displayLogo} alt="Company Logo" />}
    </div>
  );
}

export default StampPreview;
