import React, { useEffect, useState } from "react";
import StampForm from "./StampForm";
import StampPreview from "./StampPreview";
import StampSubmit from "./StampSubmit";

function MainController() {
  const [jobName, setJobName] = useState("");
  const [jobCode, setJobCode] = useState("");
  const [boxFolder, setBoxFolder] = useState("");
  const [boxFolderNumber, setBoxFolderNumber] = useState("");
  const [preparedFor, setPreparedFor] = useState("");
  const [preparedBy, setPreparedBy] = useState("");
  const initialDate = {
    year: new Date().getFullYear(),
    month: new Date().getMonth() + 1,
    day: new Date().getDate(),
  };
  const [date, setDate] = useState(initialDate);
  const [isRevision, setIsRevision] = useState(false);
  const [dateFormat, setDateFormat] = useState("MM/DD/YYYY");
  const [jobPhase, setJobPhase] = useState("");
  const [gradientStyle, setGradientStyle] = useState("No Gradient");
  const [revisionNumber, setRevisionNumber] = useState(0);

  useEffect(() => {
    const extractFolderNumber = () => {
      const trimmedUrl = boxFolder.trim();
      const match = trimmedUrl.match(/https:\/\/box\.com\/folder\/(\d+)/);
      if (match) {
        setBoxFolderNumber(String(match[1]));
      } else {
        setBoxFolderNumber(undefined);
      }
    };
    if (boxFolder) {
      extractFolderNumber();
    } else {
      setBoxFolderNumber(undefined);
    }
  }, [boxFolder]);

  const formatDateObj = (dateObj) => {
    if (!dateObj || !dateObj.year || !dateObj.month || !dateObj.day) {
      return { year: "", month: "", day: "" };
    }
    return {
      year: dateObj.year,
      month: dateObj.month,
      day: dateObj.day,
    };
  };

  const handleSubmit = () => {
    const formattedDate = formatDateObj(date);

    let preparedByNumber;
    if (preparedBy === "Eos Lightmedia") {
      preparedByNumber = 0;
    } else if (preparedBy === "Abernathy Lighting Design") {
      preparedByNumber = 1;
    }

    let dateFormatNumber;
    switch (dateFormat) {
      case "YYYY/MM/DD":
        dateFormatNumber = 0;
        break;
      case "MM/DD/YYYY":
        dateFormatNumber = 1;
        break;
      case "DD/MM/YYYY":
        dateFormatNumber = 2;
        break;
      default:
        dateFormatNumber = -1;
    }

    console.log("Job Name:", jobName);
    console.log("Job Code:", jobCode);
    console.log("Box Folder Number:", boxFolderNumber);
    console.log("Prepared For:", preparedFor);
    console.log("Prepared By:", preparedByNumber);
    console.log("Date:", formattedDate);
    console.log("Is Revision:", isRevision);
    console.log("Job Phase:", jobPhase);
    console.log("Gradient Style:", gradientStyle);
    console.log("Date Format:", dateFormatNumber);
    console.log("Revision Number:", revisionNumber);
  };

  return (
    <div className="all-app-content">
      <StampForm
        jobName={jobName}
        setJobName={setJobName}
        jobCode={jobCode}
        setJobCode={setJobCode}
        boxFolder={boxFolder}
        setBoxFolder={setBoxFolder}
        boxFolderNumber={boxFolderNumber}
        setBoxFolderNumber={setBoxFolderNumber}
        preparedFor={preparedFor}
        setPreparedFor={setPreparedFor}
        preparedBy={preparedBy}
        setPreparedBy={setPreparedBy}
        date={date}
        setDate={setDate}
        isRevision={isRevision}
        setIsRevision={setIsRevision}
        jobPhase={jobPhase}
        setJobPhase={setJobPhase}
        gradientStyle={gradientStyle}
        setGradientStyle={setGradientStyle}
        dateFormat={dateFormat}
        setDateFormat={setDateFormat}
        revisionNumber={revisionNumber}
        setRevisionNumber={setRevisionNumber}
      />
      <StampPreview
        jobName={jobName}
        jobCode={jobCode}
        boxFolder={boxFolder}
        boxFolderNumber={boxFolderNumber}
        preparedFor={preparedFor}
        preparedBy={preparedBy}
        date={date}
        isRevision={isRevision}
        jobPhase={jobPhase}
        gradientStyle={gradientStyle}
        dateFormat={dateFormat}
        revisionNumber={revisionNumber}
      />
      <StampSubmit onSubmit={handleSubmit} />
    </div>
  );
}

export default MainController;
