import React from "react"

const PackageSettings = ({ isPackagePDFs, setIsPackagePDFs }) => {
  const handleRadioChange = (event) => {
    setIsPackagePDFs(event.target.value === "yes")
  }

  return (
    <div>
      <h2>Package PDFs</h2>
      <div>
        <input
          type="radio"
          id="yes"
          name="packagePDFs"
          value="yes"
          checked={isPackagePDFs}
          onChange={handleRadioChange}
        />
        <label htmlFor="yes">Yes</label>
      </div>
      <div>
        <input
          type="radio"
          id="no"
          name="packagePDFs"
          value="no"
          checked={!isPackagePDFs}
          onChange={handleRadioChange}
        />
        <label htmlFor="no">No</label>
      </div>
      <div>
        {isPackagePDFs ? (
          <p>All PDFs will be packaged into a single document</p>
        ) : (
          <p>PDFs will be stamped individually</p>
        )}
      </div>
    </div>
  )
}

export default PackageSettings
