import React from "react"

const Disclaimer = ({ disclaimerOptions, setDisclaimer }) => {
  const handleOptionChange = (index) => {
    if (index === -1) {
      // Handle "No Disclaimer" option
      setDisclaimer(
        disclaimerOptions.map((option) => ({ ...option, checked: false }))
      )
    } else {
      // Update specific disclaimer
      const updatedDisclaimer = disclaimerOptions.map((option, idx) => ({
        ...option,
        checked: idx === index ? !option.checked : option.checked,
      }))
      setDisclaimer(updatedDisclaimer)
    }
  }

  // Check if no disclaimer is selected (all false)
  const isNoDisclaimerSelected = disclaimerOptions.every(
    (option) => !option.checked
  )

  return (
    <div className="disclaimer-container">
      <label className="input-label">Disclaimer</label>
      <div className="disclaimer-options">
        <label>
          <input
            type="checkbox"
            value="No Disclaimer"
            checked={isNoDisclaimerSelected}
            onChange={() => handleOptionChange(-1)}
          />
          No Disclaimer
        </label>
        <br />
        {disclaimerOptions.map((option, index) => (
          <div className="checkbox-options" key={index}>
            <label>
              <input
                type="checkbox"
                value={option.label}
                checked={option.checked}
                onChange={() => handleOptionChange(index)}
              />
              {option.label}
            </label>
          </div>
        ))}
      </div>
    </div>
  )
}

export default Disclaimer
