import React from "react"

const Disclaimer = ({ disclaimer, setDisclaimer }) => {
  const handleOptionChange = (index) => {
    // Create a new array with the toggled value at the specified index
    const updatedDisclaimer = disclaimer.map((item, idx) =>
      idx === index ? !item : item
    )
    setDisclaimer(updatedDisclaimer)
  }

  const labelAlign = {
    alignItems: "start",
  }

  return (
    <div className="disclaimer-container">
      <label className="input-label" style={labelAlign}>
        Disclaimer
      </label>
      <div className="disclaimer-options">
        {[1, 2, 3].map((number, index) => (
          <div key={index}>
            <label>
              <input
                type="checkbox"
                value={`Disclaimer ${number}`}
                checked={disclaimer[index]}
                onChange={() => handleOptionChange(index)}
              />
              {`Disclaimer ${number}`}
            </label>
            <br />
          </div>
        ))}
      </div>
    </div>
  )
}

export default Disclaimer
