import React from "react"

const Disclaimer = ({ disclaimer, setDisclaimer }) => {
  const handleOptionChange = (index) => {
    if (index === -1) {
      // Handle "No Disclaimer" option
      setDisclaimer(disclaimer.map(() => false))
    } else {
      // Update specific disclaimer
      const updatedDisclaimer = disclaimer.map((item, idx) =>
        idx === index ? !item : item
      )
      // If any disclaimer is selected, ensure "No Disclaimer" is unchecked
      if (updatedDisclaimer.some((val) => val)) {
        updatedDisclaimer[-1] = false
      }
      setDisclaimer(updatedDisclaimer)
    }
  }

  const isNoDisclaimerSelected = disclaimer.every((item) => !item)

  return (
    <div className="disclaimer-container">
      <label className="input-label">Add Disclaimer</label>
      <div className="disclaimer-options">
        <label>
          <input
            type="checkbox"
            value="No Disclaimer"
            checked={isNoDisclaimerSelected}
            onChange={() => handleOptionChange(-1)}
          />
          No Disclaimer
        </label>
        <br />
        {[1, 2, 3].map((number, index) => (
          <div className="checkbox-options" key={index}>
            <label>
              <input
                type="checkbox"
                value={`Disclaimer ${number}`}
                checked={disclaimer[index]}
                onChange={() => handleOptionChange(index)}
              />
              {`Disclaimer ${number}`}
            </label>
            <br />
          </div>
        ))}
      </div>
    </div>
  )
}

export default Disclaimer
