import React, { useState } from "react"
import axios from "axios"

function URLFolderSelector({
  URLFolder,
  setURLFolder,
  setFoundPDFs,
  foundPDFs,
}) {
  const defaultHelperText =
    "Paste Box link here (e.g. https://eoslightmedia.app.box.com/folder/240776517305)"
  const [helperText, setHelperText] = useState(defaultHelperText)

  const mockData = {
    items: [
      {
        name: "archive",
        type: "folder",
      },
      {
        name: "Gobos",
        type: "folder",
      },
      {
        name: "Stamp Exports",
        type: "folder",
      },
      {
        name: "zzCUT",
        type: "folder",
      },
      {
        name: "L01 - LiteLab a01.pdf",
        type: "pdf",
      },
      {
        name: "L02 - LiteLab a04.pdf",
        type: "pdf",
      },
      {
        name: "L04 - Gantom DMX Spot.pdf",
        type: "pdf",
      },
      {
        name: "L05 - DLC LumiSheet.pdf",
        type: "pdf",
      },
      {
        name: "L06 - Altman Micro Par.pdf",
        type: "pdf",
      },
      {
        name: "L06a - Soraa Par 20 lamp.pdf",
        type: "pdf",
      },
      {
        name: "T01 - LiteLab bus- 08h.pdf",
        type: "pdf",
      },
      {
        name: "T02 - LiteLab bus-13.pdf",
        type: "pdf",
      },
    ],
    path: "02-Projects/PMV - Port Metro Vancouver Upgrades/01-Design/05-Cutsheets/LX/New Spec",
  }

  const fetchFolderData = async (folderNumber) => {
    if (process.env.NODE_ENV === "development") {
      // If in development, use mock data
      const pdfs = mockData.items.filter((item) => item.type === "pdf")
      console.log(pdfs)
      setFoundPDFs(pdfs)
      console.log(foundPDFs)
      console.log(mockData.path)
      setHelperText(`Path: ${mockData.path}`)
    } else {
      // In production, make an API call
      try {
        const response = await axios.get(`/api/folder/${folderNumber}`)
        const { items, path } = response.data
        const pdfs = items.filter((item) => item.type === "pdf")
        setFoundPDFs(pdfs)
        setHelperText(`Path: ${path}`)
      } catch (error) {
        setHelperText("Error fetching folder data.")
        console.error("Error fetching folder data:", error)
      }
    }
  }

  const validateFolderLink = (inputUrl) => {
    setURLFolder(inputUrl)

    if (inputUrl === "") {
      setHelperText(defaultHelperText)
      return
    }

    const regex = /^(https?:\/\/)?eoslightmedia.app.box.com\/folder\/(\d+)$/
    const match = inputUrl.match(regex)

    if (match && match[2]) {
      fetchFolderData(match[2])
      setHelperText("Fetching folder data...")
    } else {
      setHelperText("Hmmm, something about that link doesn’t look right...")
    }
  }

  const handleChange = (event) => {
    const inputUrl = event.target.value
    validateFolderLink(inputUrl)
  }

  return (
    <div className="box-folder-selector-container">
      <label htmlFor="box-folder-url" className="input-label">
        Box Folder Link:
      </label>
      <input
        type="text"
        id="box-folder-url"
        className="text-input"
        value={URLFolder}
        onChange={handleChange}
        placeholder="https://eoslightmedia.app.box.com/folder/240776517305"
      />
      <p className="helper-text">{helperText}</p>
    </div>
  )
}

export default URLFolderSelector
