import React, { useState, useEffect } from "react"

const Banner = ({ createdFolderNumber, setBannerIsVisible }) => {
  const [bannerMessage, setBannerMessage] = useState("")
  const [bannerStyle, setBannerStyle] = useState({})

  useEffect(() => {
    if (createdFolderNumber) {
      setBannerMessage(
        <>
          Success! Click <a href={`/${createdFolderNumber}`}>here</a> to find
          your stamped Cut Sheets.
        </>
      )
      setBannerStyle({
        backgroundColor: "#4CAF50", // Green for success
        color: "#fff", // White text color
      })
    } else {
      setBannerMessage(
        "Uh oh... looks like something went wrong. Please check your settings and try again"
      )
      setBannerStyle({
        backgroundColor: "#F44336", // Red for error
        color: "#fff", // White text color
      })
    }
  }, [createdFolderNumber])

  return (
    <div className="banner-main-div" style={bannerStyle}>
      <div className="banner-inner-div">
        <p>{bannerMessage}</p>
      </div>
    </div>
  )
}

export default Banner
