import React from "react"

const PackageSettings = ({ isPackagePDFs, setIsPackagePDFs }) => {
  return (
    <div className="gradient-selector-container">
      <label className="input-label">Package PDFs?</label>
      <div className="button-group">
        <button
          onClick={() => setIsPackagePDFs(false)}
          className={!isPackagePDFs ? "selected" : ""}
        >
          No
        </button>
        <button
          onClick={() => setIsPackagePDFs(true)}
          className={isPackagePDFs ? "selected" : ""}
        >
          Yes
        </button>
      </div>
      <div>
        {isPackagePDFs ? (
          <p className="helper-text">
            All PDFs will be packaged into a single document
          </p>
        ) : (
          <p className="helper-text">PDFs will be stamped individually</p>
        )}
      </div>
    </div>
  )
}

export default PackageSettings
