import React, { useState, useEffect } from "react"

function ProcessingPage({ isProcessing }) {
  const [secondsRemaining, setSecondsRemaining] = useState(30)

  useEffect(() => {
    // Only set the interval if secondsRemaining is greater than 0
    if (secondsRemaining > 0) {
      const timerId = setInterval(() => {
        setSecondsRemaining(secondsRemaining - 1)
      }, 1000)

      // Clear the interval when the component is unmounted or the countdown is finished
      return () => clearInterval(timerId)
    }
  }, [secondsRemaining]) // Dependency array ensures the effect runs when secondsRemaining changes

  return (
    <div className="processing-page-div">
      <div className="processing-page-content">
        <p>Hang tight while your cut sheets are being stamped...</p>
        <p className="countdown-timer">{secondsRemaining}</p>
      </div>
    </div>
  )
}

export default ProcessingPage
