import React from "react";

function StampPreview(props) {
  const {
    jobName,
    jobCode,
    preparedFor,
    preparedBy,
    date,
    isRevision,
    jobPhase,
    gradientStyle,
    dateFormat,
    revisionNumber,
    boxFolder,
    boxFolderNumber,
  } = props;

  const formatDisplayDate = (date, format) => {
    if (!date || !date.year || !date.month || !date.day) {
      return "No Date Selected";
    }

    const paddedMonth = String(date.month).padStart(2, "0");
    const paddedDay = String(date.day).padStart(2, "0");

    switch (format) {
      case "YYYY/MM/DD":
        return `${date.year}/${paddedMonth}/${paddedDay}`;
      case "MM/DD/YYYY":
        return `${paddedMonth}/${paddedDay}/${date.year}`;
      case "DD/MM/YYYY":
        return `${paddedDay}/${paddedMonth}/${date.year}`;
      default:
        return `${date.year}-${paddedMonth}-${paddedDay}`; // Default format
    }
  };

  const displayDate = formatDisplayDate(date, dateFormat);

  return (
    <div>
      <h2>Stamp Preview</h2>
      <p>Job Name: {jobName}</p>
      <p>Job Code: {jobCode}</p>
      <p>Prepared For: {preparedFor}</p>
      <p>Prepared By: {preparedBy}</p>
      <p>Date: {displayDate}</p>
      <p>Date Format: {dateFormat}</p>
      <p>Is Revision: {isRevision ? "Yes" : "No"}</p>
      <p>Job Phase: {jobPhase}</p>
      <p>Gradient Style: {gradientStyle}</p>
      <p>Revision Number: {revisionNumber}</p>
      <p>Box Folder: {boxFolder}</p>
      <p>Box Folder Number: {boxFolderNumber}</p>
    </div>
  );
}

export default StampPreview;
